import React from 'react';
import './App.css';
import OffersList from './Components/OffersList/OffersList';
import AddOfferForm from './Components/AddOfferForm/AddOfferForm';

function App() {

  let showAddOffer = false;
  const showAddOfferHandler = () => {
    showAddOffer = !showAddOffer;
  }
  return (
    <div className="App">
      {showAddOffer ? <AddOfferForm /> : null}
      <button onClick={showAddOfferHandler}>Add new offer</button>

      <OffersList />
    </div>
  );
}

export default App;
