import React, { Component } from 'react';
import SingleOffer from './SingleOffer';
import './OffersList.css'

class OffersList extends Component {
    state = {offers:[]};

    componentDidMount(){
        fetch('http://localhost:3000/get-offer-list')
            .then(response => response.json())
            .then(data => {
                // this.setState({
                //     ...data
                // });
                console.log(data);
                this.setState({offers: data})
            })
            .catch(err => console.log(err));
    };

    render(){
        const offers = [...this.state.offers];

        const list = offers.map(offer => <SingleOffer props={offer} key={offer}/>);
        return (
            <table>
                <thead>
                    <tr>
                        <th>Title</th>
                        <th>Link</th>
                        <th>Company</th>
                        <th>Country</th>
                        <th>City</th>
                        <th>Field</th>
                        <th>Paygrade</th>
                        <th>Favorite</th>
                        <th>Applied</th>
                        <th>Stage 1</th>
                        <th>Stage 2</th>
                        <th>Got the job</th>
                    </tr>
                </thead>
                <tbody>
                    {list}
                </tbody>
            </table>
        )
    }
}

export default OffersList;
