import React, { Component } from 'react';
import './AddOfferForm.css';

class AddOfferForm extends Component {

    state = {
        formValues: {
            title: '',
            link: '',
            company: '',
            country: '',
            city: '',
            field: '',
            paygrade: '',
            favorite: false,
            applied: false,
            description: ''
        }
    };

    getFormData(){
        const formData = {};
        formData.title = document.getElementById('title').value;
        formData.link = document.getElementById('link').value;
        formData.company = document.getElementById('company').value;
        formData.country = document.getElementById('country').value;
        formData.city = document.getElementById('city').value;
        formData.field = document.getElementById('field').value;
        formData.paygrade = document.getElementById('paygrade').value;
        formData.favorite = document.getElementById('favorite').checked ? true : false;
        formData.applied = document.getElementById('applied').checked ? true : false;
        formData.description = document.getElementById('description').value;
    }

    submitFormHandler(event){
        event.preventDefault();
        const data = {...this.state.formValues};
        console.log(this.state.formValues);
        fetch("http://localhost:3000/add-offer", {
            method:'POST',
            mode: 'no-cors',
            headers: {'Content-Type': 'application/json',
            "Access-Control-Allow-Origin": "no-cors"},
            body: JSON.stringify(data)
        })
        .then(response => response.json())
        .then(data => console.log(data))
        .catch(err => console.log(err));
    }

    changeHandler(event){
        const stateCopy = {...this.state.formValues};
        const name = event.target.name;
        let value = event.target.value;
        stateCopy[name] = value;
        this.setState(
            {formValues: {...stateCopy}}
        )
    }

    render(){
        return(
            <div className="offer-form">
                <div className="offer-form__background" onClick={this.props.hide}></div>
                <form action="/add-offer" method="post" className="offer-form__form" onSubmit={this.submitFormHandler.bind(this)}>
                {/* <form action="/add-offer" method="post" className="offer-form__form"> */}
                    <label htmlFor="title">Title:</label>
                    <input type="text" id="title" name="title" required value={this.state.title} onChange={this.changeHandler.bind(this)}/>
                    <label htmlFor="link">Link:</label>
                    <input type="text" id="link" name="link" required value={this.state.link} onChange={this.changeHandler.bind(this)}/>
                    <label htmlFor="company">Company:</label>
                    <input type="text" id="company" name="company"  value={this.state.company} onChange={this.changeHandler.bind(this)}/>
                    <label htmlFor="country">Country:</label>
                    <input type="text" id="country" name="country" required value={this.state.country} onChange={this.changeHandler.bind(this)}/>
                    <label htmlFor="city">City:</label>
                    <input type="text" id="city" name="city" required value={this.state.city} onChange={this.changeHandler.bind(this)}/>
                    <label htmlFor="field">Field:</label>
                    <input type="text" id="field" name="field" value={this.state.field} onChange={this.changeHandler.bind(this)}/>
                    <label htmlFor="paygrade">Paygrade:</label>
                    <input type="text" id="paygrade" name="paygrade"  value={this.state.paygrade} onChange={this.changeHandler.bind(this)}/>
                    <label htmlFor="favorite">Favorite:</label>
                    <input type="checkbox" id="favorite" name="favorite" value={true} onChange={this.changeHandler.bind(this)}/>
                    <label htmlFor="applied">Applied:</label>
                    <input type="checkbox" id="applied" name="applied" value={true} onChange={this.changeHandler.bind(this)}/>
                    <label htmlFor="description">Description:</label>
                    <textarea id="description" name="description" rows="10" cols="30" value={this.state.description} onChange={this.changeHandler.bind(this)}></textarea>
                    <button type="submit">Add Offer</button>
                </form>
            </div>
        )
    }
}

export default AddOfferForm;
