import React, { Component } from 'react';
import './AddOfferForm.css';

class AddOfferForm extends Component {

    state = {
        formValues: {
            title: '',
            link: '',
            company: '',
            country: '',
            city: '',
            field: '',
            paygrade: '',
            favorite: '',
            applied: '',
            description: ''
        }
    };

    submitFormHandler(event){
        event.preventDefault();
        fetch("localhost:3000/add-offer", {
            method:'POST',
            mode: 'cors',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(this.state.formValues)
        })
        .then(response => response.json())
        .then(data => console.log(data))
        .catch(err => console.log(err));
    }

    changeHandler(event){
        const stateCopy = {...this.state.formValues};
        const name = event.target.name;
        const value = event.target.value;
        stateCopy[name] = value;
        this.setState(
            {formValues: {stateCopy}}
        )
    }

    render(){
        return(
            <div className="offer-form__background" onClick={this.props.hide}>

                <form action="/add-offer" method="post" className="offer-form__form" onSubmit={this.submitFormHandler.bind(this)}>
                    <label for="title">Title:</label>
                    <input type="text" id="title" name="title" required value={this.state.title} onChange={this.changeHandler.bind(this)}/>
                    <label for="link">Link:</label>
                    <input type="text" id="link" name="link" required value={this.state.link} onChange={this.changeHandler.bind(this)}/>
                    <label for="company">Company:</label>
                    <input type="text" id="company" name="company"  value={this.state.company} onChange={this.changeHandler.bind(this)}/>
                    <label for="country">Country:</label>
                    <input type="text" id="country" name="country" required value={this.state.country} onChange={this.changeHandler.bind(this)}/>
                    <label for="city">City:</label>
                    <input type="text" id="city" name="city" required value={this.state.city} onChange={this.changeHandler.bind(this)}/>
                    <label for="field">Field:</label>
                    <input type="text" id="field" name="field" required value={this.state.field} onChange={this.changeHandler.bind(this)}/>
                    <label for="paygrade">Paygrade:</label>
                    <input type="text" id="paygrade" name="paygrade"  value={this.state.paygrade} onChange={this.changeHandler.bind(this)}/>
                    <label for="favorite">Favorite:</label>
                    <input type="checkbox" id="favorite" name="favorite" value={this.state.favorite} onChange={this.changeHandler.bind(this)}/>
                    <label for="applied">Applied:</label>
                    <input type="checkbox" id="applied" name="applied" required value={this.state.applied} onChange={this.changeHandler.bind(this)}/>
                    <label for="description">Description:</label>
                    <textarea id="description" name="description" rows="10" cols="30" value={this.state.description} onChange={this.changeHandler.bind(this)}></textarea>
                    <button type="submit">Add Offer</button>
                </form>
            </div>
        )
    }
}

export default AddOfferForm;
